pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/presets/ERC721PresetMinterPauserAutoId.sol";

import "./interfaces/INidhiNFT.sol";
import "./NidhiMarket.sol";

contract NidhiNFT is ERC721, INidhiNFT {

    struct Metadata {
        string image;
        string externalURL;
        string description;
        string name;
        uint intrinsicValue;
    }

    struct StoredMetadata {
        string image;
        string description;
        string name;
    }

    uint private _tokenIds;
    address private _owner;
    string private _marketBaseURI;
    string private _tokenBaseURI;

    NidhiMarket private immutable _marketplace;

    mapping (uint => address) private _tokenCreators;
    mapping (uint => bool) private _publiclyTradeable;
    mapping (uint => StoredMetadata) private _metadata;

    event TokenCreated(
        uint indexed tokenId,
        address indexed creator,
        string name,
        string description,
        string imageURI
    );

    constructor(
        address marketplaceAddress,
        string memory marketBaseURI,
        string memory tokenBaseURI
    )
        ERC721("Nidhi Passive Income NFT", "NPI")
    {
        _marketBaseURI = marketBaseURI;
        _tokenBaseURI = tokenBaseURI;
        _owner = msg.sender;
        _marketplace = NidhiMarket(marketplaceAddress);
    }

    /**
     * @dev Creates a new token with the provided name, description
     * and image URI. Upon successful completion, the `TokenCreated`
     * event will be emitted.
     */
    function createToken(
        string memory name_,
        string memory description_,
        string memory imageURI_
    )
        external returns (uint tokenId)
    {
        address creator = msg.sender;
        tokenId = ++_tokenIds;
        _mint(creator, tokenId);
        _afterTokenTransfer(address(0), creator, tokenId);
        StoredMetadata memory meta;
        meta.name = name_;
        meta.description = description_;
        meta.image = imageURI_;
        _metadata[tokenId] = meta;
        _tokenCreators[tokenId] = creator;
        emit TokenCreated(
            tokenId,
            creator,
            name_,
            description_,
            imageURI_
        );
    }

    /**
     * @dev Allows or prevents NFT to be traded on other marketplaces.
     * Public trading will be allowed after the initial sale on Nidhi's
     * marketplace. This ensures that every Nidhi NFT has an intrinsic
     * value.
     */
    function approvePublicTrading(
        uint tokenId,
        bool approve
    ) external override {
        require(msg.sender == address(_marketplace));
        _publiclyTradeable[tokenId] = approve;
    }

    function setMarketBaseURI(string memory baseURI) external onlyOwner {
        _marketBaseURI = baseURI;
    }

    function setTokenBaseURI(string memory baseURI) external onlyOwner {
        _tokenBaseURI = baseURI;
    }

    function transferOwnership(address newOwner) external onlyOwner {
        _owner = newOwner;
    }

    /**
     * @dev Burns the token, removes all its stored data and also ensures
     * that references to it are removed from the Nidhi marketplace.
     */
    function burn(uint tokenId) external {
        require(
            _isApprovedOrOwner(msg.sender, tokenId),
            "caller is not owner nor approved"
        );
        _burn(tokenId);
        _marketplace.afterBurnToken(
            _tokenCreators[tokenId],
            address(this),
            tokenId
        );
        delete _tokenCreators[tokenId];
        delete _publiclyTradeable[tokenId];
        delete _metadata[tokenId];
    }

    function metadata(uint tokenId)
        external view
        returns (Metadata memory meta)
    {
        require(_exists(tokenId), "Meta query for nonexistent token");
        uint itemId = _marketplace.getMarketItemId(address(this), tokenId);
        StoredMetadata storage storedMeta = _metadata[tokenId];
        meta.name = storedMeta.name;
        meta.description = storedMeta.description;
        meta.image = storedMeta.image;
        if (itemId != 0) {
            meta.externalURL = string(
                abi.encodePacked(_marketBaseURI, Strings.toString(itemId))
            );
            meta.intrinsicValue = _marketplace.intrinsicValue(itemId);
        }
    }

    /**
     * @dev Returns the address of the creator of the provided token id.
     */
    function creatorOf(uint tokenId) external view override returns (address creator) {
        creator = _tokenCreators[tokenId];
        require(creator != address(0), "invalid token id");
    }

    function _baseURI() internal view override returns (string memory) {
        return _tokenBaseURI;
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal virtual override
    {
        require(
            from == address(0) || to == address(0) || _publiclyTradeable[tokenId],
            "public trading not allowed"
        );
    }

    function _transfer(address from, address to, uint256 tokenId)
        internal virtual override
    {
        super._transfer(from, to, tokenId);
        _afterTokenTransfer(from, to, tokenId);
    }

    function _afterTokenTransfer(address from, address to, uint256 tokenId)
        private
    {
        if (from != address(_marketplace) && to != address(_marketplace) && from != to) {
            if (from != address(0)) {
                _marketplace.removeTokenFromOwnerCollection(address(this), tokenId);
            }
            if (to != address(0)) {
                _marketplace.importMarketItem(address(this), tokenId);
            }
            if (from == address(0)) {
                _marketplace.addTokenToCreatorCollection(to, address(this), tokenId);
            }
        }
    }

    modifier onlyOwner() {
        require(_owner == msg.sender, "caller is not the owner");
        _;
    }
}","keccak256":"0xbc8f08e910dcab172c97ab74931ede7b9341ec291540507cde9ef53a96ecec20","license":"AGPL-3.0-or-later"},"contracts/NidhiProfile.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0-or-later
pragma solidity ^0.8.4;

contract NidhiProfile {

    struct Profile {
        string name;
        string imageURL;
        string deeplink;
        string description;
        string url;
        string twitter;
        string instagram;
        string medium;
    }

    mapping (address => Profile) public userProfiles;
    mapping (string => address) public deeplinkToAddress;

    function update(Profile memory profile)
        external validDeeplink(profile.deeplink)
    {
        address owner = msg.sender;
        string storage currentDeeplink = userProfiles[owner].deeplink;
        if (bytes(currentDeeplink).length != 0) {
            delete deeplinkToAddress[currentDeeplink];
            deeplinkToAddress[profile.deeplink] = owner;
        } else if (bytes(profile.deeplink).length != 0) {
            deeplinkToAddress[profile.deeplink] = owner;
        }
        userProfiles[owner] = profile;
    }

    function remove() external {
        delete deeplinkToAddress[userProfiles[msg.sender].deeplink];
        delete userProfiles[msg.sender];
    }

    function nameOf(address owner) external view returns (string memory) {
        return userProfiles[owner].name;
    }

    function deeplinkOf(address owner) external view returns (string memory) {
        return userProfiles[owner].deeplink;
    }

    modifier validDeeplink(string memory deeplink) {
        if (bytes(deeplink).length != 0) {
            address currentAddress = deeplinkToAddress[deeplink];
            require(
                currentAddress == address(0) || currentAddress == msg.sender,
                "deeplink already in use"
            );
        }
        _;
    }
}","keccak256":"0x553c337bc1aad6b4dd125b4fae826c9558a2cf6377563f1f5b899c59cae09ba2","license":"AGPL-3.0-or-later"},"contracts/external/IRouter.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0-or-later
